package com.icure.kryptom.crypto.external

@JsExport
external interface XStrongRandom {
	/**
	 * Generates a sequence of random bytes.
	 * @param length amount of bytes to generate
	 * @return the generated bytes.
	 */
	fun randomBytes(length: Int): ByteArray

	// TODO investigate possibility of having native implementation of fill when going through react-native/expo

	/**
	 * Generates a random UUID.
	 * @return string representation of the uuid.
	 */
	fun randomUUID(): String
}